// SPDX-License-Identifier: MIT
pragma solidity ^0.8.10;

/**
 * @dev Interface of the StarShip
 StarShip will be implemented as an NFT contract.
 */
interface IStarShip {
      /**
     * @dev  query  user's all starships
     * 
       @return an uint256[] contains all starships belongs to the user .
       the even number position is corresponding id, the odd number position  is the starship data
       example [0,35546,1,27513654,6,355455] mean there are starships  [{id:0,data:35546},{id:1,data:27513654},{id:6,data:355455}]
       the data parser will be provided in another js script or use the nft tokeUri to get the information
     */
    function queryUserStarShips(address user) external view returns (uint256[] memory);

    /**
      @dev query a ship is whether a basic ship
      @return true if it's a basic ship,false if it's a advanced ship
     */

    function isBasicShip(uint256 nftId) external view returns (bool);

    function getDataByNftId(uint256 nftId) external view returns (uint256);


    function queryNameId(uint256 nftId) view external returns (uint16);

    function queryLevel(uint256 nftId) view external returns (uint8);

    function queryDuration(uint256 nftId) view external returns (uint16);

    function queryBasicSlotLimit(uint256 nftId) view external returns (uint8);

    function queryBasicSlotCurrent(uint256 nftId) view external returns (uint8);

    function queryAdvanceSlotLimit(uint256 nftId,uint256 identityOfSlot) view external returns (uint8);

    function queryAdvanceSlotCurrent(uint256 nftId,uint256 identityOfSlot) view external returns (uint8);
    
    function queryStarShipMinedQuarkAndFireAndDurability(uint256 nftId) view external returns(uint256[] memory);

}

